mapper.select:
/* ID : MainVideo_sql.MainVideo_MainVideoListCnt DEC : 메인피드 컨텐츠 리스트 Cnt 조회 */ SELECT COUNT(*) FROM ( SELECT a.MAIN_VIDEO_IDX, a.FILE_CONTENT_IDX, b.FILE_NAME, a.ORDER_SEQ, a.ACTIVE_YN, a.MOD_DATE FROM main_video a INNER JOIN file_content b ON a.FILE_CONTENT_IDX = b.FILE_CONTENT_IDX WHERE IFNULL(a.DEL_YN,'N') = 'N' AND IFNULL(b.DEL_YN,'N') = 'N' AND b.cont_type = 'V'
AND b.FILE_NAME LIKE CONCAT('%',#{sSearch},'%')
) t

mapper.insert:

        SELECT D.ORDER_SEQ FROM (SELECT (SELECT ifnull(MAX(ORDER_SEQ) + 1, '1') FROM main_video WHERE IFNULL(DEL_YN,'N') = 'N') AS ORDER_SEQ) AS D      

	INSERT INTO main_video(
			FILE_CONTENT_IDX,
			ORDER_SEQ,
			REG_USER,
			REG_DATE,
			MOD_USER,
			MOD_DATE
	)
	VALUES 
	(
	    #{fileContentIdx}
	  , #{orderSeq}
	  , #{regUser}
	  , now()
	  , #{regUser}
	  , now()
	)

mapper.update:
		/*
		  ID  : MainVideo_sql.MainVideo_Delete
		  DEC : 메인피드 이미지 삭제
		*/
		UPDATE main_video
		SET    DEL_YN = 'Y'
		     , MOD_USER = #{modUser}
		     , MOD_DATE = now()
		 WHERE MAIN_VIDEO_IDX IN

            #{contentIdx}

/*
  ID  : MainVideo_sql.MainVideo_Delete
  DEC : 메인피드 이미지 비활성화
*/
UPDATE main_video
SET    ACTIVE_YN = #{activeYn}
     , MOD_USER = #{modUser}
     , MOD_DATE = now()
 WHERE MAIN_VIDEO_IDX = #{mainVideoIdx}

/*
  ID  : MainVideo_sql.MainVideo_SortUpdate
  DEC : 메인비디오 순서 변경
*/
UPDATE main_video
SET    ORDER_SEQ = #{orderSeq}
     , MOD_USER = #{modUser}
     , MOD_DATE = now()
 WHERE MAIN_VIDEO_IDX = #{mainVideoIdx}
